(321, 'GImage', 'Widget', 'BillBoard Image', NULL, '', 'a:3:{s:11:"htmlOptions";a:1:{s:5:"class";s:20:"homeRight pull-right";}s:3:"src";s:42:"/themes/ipp-base/images/home_power-img.png";s:3:"alt";s:13:"Energy Images";}', '', '', '*', '2013-02-17', '2013-07-22', 1, 314),
(324, 'GTag', 'Widget', 'Main Content Heading', NULL, 'About Us', 'a:1:{s:3:"tag";s:2:"h2";}', '', '', '*', '2013-02-17', '2013-01-07', 0, 303),
(325, 'GTag', 'Widget', '1''st Paragraph', '', 'South Africa has a high level of Renewable Energy potential and presently has in place a target of 10 000 GWh of Renewable Energy. The Minister has determined that 3 725 megawatts (MW) to be generated from Renewable Energy sources is required to ensure the continued uninterrupted supply of electricity. This 3 725 MW is broadly in accordance with the capacity allocated to Renewable Energy generation in IRP 2010-2030. The <a target="_blank" rel="nofollow" href="http://www.ipprenewables.co.za">IPP Procurement Programme</a>&nbsp;was designed so as to contribute towards the target of 3 725 megawatts and towards socio-economic and environmentally sustainable growth, and to start and stimulate the renewable industry in South Africa. The IPP Procurement Programme was launched by the Department in August 2011.', 'a:2:{s:11:"htmlOptions";a:1:{s:5:"class";s:11:"textJustify";}s:3:"tag";s:1:"p";}', 'file_manager', '', '*', '2013-02-17', '2013-03-10', 0, 303),
